INSERT INTO academic_programs (id, program_title, short_description, full_description, main_image, category, is_active, display_order, created_at, updated_at) VALUES ('b716dc35-1af6-463a-b491-74d94deb891a', 'Pre-School Education', 'Early childhood development and learning foundation', 'Our pre-school program focuses on holistic development of children aged 3-5 years, providing a nurturing environment that promotes cognitive, social, emotional, and physical growth through play-based learning and structured activities.', NULL, 'pre-school', TRUE, 1, '2025-09-18T03:34:09.198Z', '2025-09-18T03:34:09.198Z');
