INSERT INTO academic_programs (id, program_title, short_description, full_description, main_image, category, is_active, display_order, created_at, updated_at) VALUES ('ffa7149c-4b49-46b9-9d41-11f0f201db01', 'High School Education', 'Comprehensive secondary education', 'High school program for grades 9-10 provides comprehensive education following state board curriculum. Students prepare for board examinations while developing leadership skills, career awareness, and academic excellence.', NULL, 'high-school', TRUE, 4, '2025-09-18T03:34:09.198Z', '2025-09-18T03:34:09.198Z');
